$contents = explode('<br />'.PHP_EOL.'<br />', $contents);

$news = array();

for ($i = 0; $i < 50; $i++) {

	$rand_year = rand(2014, 2015);
	$rand_month = sprintf('%1$02d', $rand_year == 2015 ? rand(1, 10) : rand(1, 12));
	$rand_day = sprintf('%1$02d', rand(1, 29));
	$rand_hour = sprintf('%1$02d', rand(0, 23));
	$rand_minute = sprintf('%1$02d', rand(0, 59));
	$rand_second = sprintf('%1$02d', rand(0, 59));

	$rand_date = $rand_year.'-'.$rand_month.'-'.$rand_day.' '.$rand_hour.':'.$rand_minute.':'.$rand_second;

	//echo $rand_date.'<br>';

	$news_text = str_replace(';', '.', trim($contents[$i]));
	$first_space_pos = strpos($news_text, ' ');
	$first_point_pos = strpos($news_text, '.');

	$news_author = substr($news_text, 0, $first_space_pos);
	$news_title = substr($news_text, $first_space_pos + 1, $first_point_pos - $first_space_pos -1 );
	$news_text = substr($news_text, $first_point_pos + 2);

	$news[] = array(
		'news_author' => $news_author,
		'news_date' => $rand_date,
		'news_text' => ucfirst($news_text),
		'news_title' => ucfirst(trim($news_title))
	);
}

